function startRecording(data) {
  const { host, port, language, task, modelSize, useVad } = data;
  const ws = new WebSocket(`ws://${host}:${port}`);
  let isServerReady = false;
  let isCapturing = false;
  let audioContext = null;
  let mediaStream = null;
  let mediaRecorder = null;
  let audioChunks = [];
  let transcriptionId = null;

  ws.onopen = () => {
    console.log('WebSocket connection established');
    browser.runtime.sendMessage({ type: 'serverStatus', status: 'connected' });
    ws.send(JSON.stringify({
      task,
      language,
      modelSize,
      useVad,
    }));
  };

  ws.onmessage = (event) => {
    const message = JSON.parse(event.data);
    if (message.type === 'ready') {
      isServerReady = true;
      browser.runtime.sendMessage({ type: 'serverStatus', status: 'ready' });
    } else if (message.type === 'transcriptionId') {
      transcriptionId = message.transcriptionId;
      browser.runtime.sendMessage({ type: 'transcriptionId', transcriptionId });
    } else if (message.type === 'transcription') {
      browser.runtime.sendMessage({ type: 'transcription', transcription: message.transcription });
    } else if (message.type === 'status') {
      browser.runtime.sendMessage({ type: 'serverStatus', status: message.status });
    }
  };

  ws.onerror = (error) => {
    console.error('WebSocket error:', error);
    browser.runtime.sendMessage({ type: 'serverStatus', status: 'error' });
  };

  ws.onclose = () => {
    console.log('WebSocket connection closed');
    browser.runtime.sendMessage({ type: 'serverStatus', status: 'disconnected' });
  };

  function startCapture() {
    if (!isServerReady || isCapturing) return;
    isCapturing = true;
    navigator.mediaDevices.getUserMedia({ audio: true })
      .then((stream) => {
        audioContext = new AudioContext();
        mediaStream = stream;
        const source = audioContext.createMediaStreamSource(stream);
        const processor = audioContext.createScriptProcessor(4096, 1, 1);
        source.connect(processor);
        processor.connect(audioContext.destination);

        processor.onaudioprocess = (event) => {
          if (!isCapturing) return;
          const audioBuffer = event.inputBuffer;
          const audioData = new Float32Array(audioBuffer.getChannelData(0));
          audioChunks.push(audioData);
        };

        browser.runtime.sendMessage({ type: 'serverStatus', status: 'capturing' });
      })
      .catch((error) => {
        console.error('Error accessing microphone:', error);
        browser.runtime.sendMessage({ type: 'serverStatus', status: 'error' });
      });
  }

  function stopCapture() {
    if (!isCapturing) return;
    isCapturing = false;
    if (audioContext && mediaStream) {
      mediaStream.getTracks().forEach((track) => track.stop());
      audioContext.close();
      audioContext = null;
      mediaStream = null;
    }
    if (mediaRecorder) {
      mediaRecorder.stop();
      mediaRecorder = null;
    }
    audioChunks = [];
    browser.runtime.sendMessage({ type: 'serverStatus', status: 'stopped' });
  }

  browser.runtime.onMessage.addListener((request, sender, sendResponse) => {
    if (request.type === 'start') {
      startCapture();
    } else if (request.type === 'stop') {
      stopCapture();
    }
  });
}
