      this.ws.send(JSON.stringify({ type: 'run', code }));
      this.ws.onmessage = (event) => {
        const data = JSON.parse(event.data);
        if (data.type === 'result') {
          this.running = false;
          resolve(data.result);
        } else if (data.type === 'error') {
          this.running = false;
          reject(data.error);
        }
      };
    });
