    }
    const id = Math.random().toString(36).substr(2, 9);
    const msg = {
      id,
      method,
      path,
      querystring,
    };
    return new Promise((resolve, reject) => {
      this.ev.on(id, (res) => {
        if (res.status === 200) {
          resolve(res);
        } else {
          reject(res);
        }
      });
