    this.wsClient = null;
    this.sendQueue = [];
    this.pendingReconnect = null;
  }

  fireSendQueue() {
    if (this.wsClient && this.wsClient.readyState === WebSocket.OPEN) {
      this.sendQueue.forEach((requestContext) => {
        this.wsClient.send(requestContext.message);
        requestContext.resolve();
      });
      this.sendQueue = [];
    }
  }

  clearSendQueue() {
    this.sendQueue.forEach((requestContext) => {
      requestContext.reject(new Error('Connection closed'));
    });
    this.sendQueue = [];
  }

  planToReconnect(rpcUrl, options) {
    if (this.pendingReconnect) {
      clearTimeout(this.pendingReconnect);
    }
    this.pendingReconnect = setTimeout(() => {
      this.reconnect(rpcUrl, options);
    }, options.reconnectInterval);
  }

  init() {
    this.wsClient = null;
    this.clearSendQueue();
    if (this.pendingReconnect) {
      clearTimeout(this.pendingReconnect);
      this.pendingReconnect = null;
    }
  }

  connect(rpcUrl, options, onOpenCallback, onCloseCallback, onMessageCallback) {
    this.init();
    this.wsClient = new WebSocket(rpcUrl, options);
    this.wsClient.onopen = onOpenCallback;
    this.wsClient.onclose = (event) => {
      onCloseCallback(event);
      this.planToReconnect(rpcUrl, options);
    };
    this.wsClient.onmessage = onMessageCallback;
  }

  reconnect(rpcUrl, options) {
    this.connect(rpcUrl, options, options.onOpenCallback, options.onCloseCallback, options.onMessageCallback);
  }

  send(requestContext) {
    return new Promise((resolve, reject) => {
      requestContext.resolve = resolve;
      requestContext.reject = reject;
      if (this.wsClient && this.wsClient.readyState === WebSocket.OPEN) {
        this.wsClient.send(requestContext.message);
        resolve();
      } else {
        this.sendQueue.push(requestContext);
      }
    });
