async function getSubscribedChannels(lang = 'id') {
  try {
    // Step 1: Scrape initial configuration data from a webpage
    const response = await axios.get('https://example.com/config');
    const $ = cheerio.load(response.data);
    const csrfToken = $('meta[name="csrf-token"]').attr('content');
    const apiKey = $('meta[name="api-key"]').attr('content');

