    const authResponse = await axios.post('https://example.com/auth', {
      csrf_token: csrfToken,
      api_key: apiKey,
    });

    const authToken = authResponse.data.token;

    // Step 3: Fetch subscribed channel packages
    const packagesResponse = await axios.get('https://example.com/packages', {
      headers: {
        Authorization: `Bearer ${authToken}`,
      },
    });

    const packages = packagesResponse.data;

    // Step 4: Process and deduplicate channel information
