        if (!channels.some((c) => c.site_id === channel.site_id)) {
          channels.push({
            lang,
            site_id: channel.site_id,
            name: channel.name,
          });
        }
      });
    });

    // Step 5: Return formatted channel data
    return channels;
  } catch (error) {
