const express = require('express');
const rest = require('connect-rest');
const bodyParser = require('body-parser');
const Attraction = require('./models/Attraction');
const domain = require('domain');

module.exports = function() {
  const app = express();
  const d = domain.create();

  app.use(bodyParser.json());

  d.on('error', function(err) {
    console.error('An uncaught error occurred:', err.stack);
    process.exit(1);
  });

  d.run(function() {
    app.use(rest.rester({
      context: '/api',
      services: {
        attractions: {
          get: function(req) {
            return Attraction.find({ approved: true });
          },
          post: function(req) {
            const { name, description, location, email } = req.body;
            const attraction = new Attraction({ name, description, location, email });
            return attraction.save().then(attraction => attraction._id);
          }
        },
        'attraction/:id': {
          get: function(req) {
            return Attraction.findById(req.params.id);
          }
        }
      }
    }));
  });

  const server = app.listen(process.env.PORT, function() {
    console.log('Server listening on port', server.address().port);
  });

  return server;
};
