const express = require('express');
const path = require('path');
const hbs = require('express-handlebars');

module.exports = function getServer(P) {
  const app = express();
  const port = P || process.env.PORT || 8000;
  const isReferenceMode = port === 8000;

  app.engine('hbs', hbs({
    extname: '.hbs',
    layoutsDir: path.join(__dirname, isReferenceMode ? 'reference' : 'development', 'templates'),
    partialsDir: path.join(__dirname, isReferenceMode ? 'reference' : 'development', 'templates', 'partials'),
    defaultLayout: 'main',
  }));
  app.set('view engine', 'hbs');

  app.use(express.static(path.join(__dirname, isReferenceMode ? 'reference' : 'development', 'public')));

  app.get('/component/:component', (req, res) => {
    const component = req.params.component;
    const config = require(path.join(__dirname, isReferenceMode ? 'reference' : 'development', 'config', `${component}.json`));
    res.render('component', { component, config });
  });

  app.listen(port, () => {
    process.title = `Regression Test Server${isReferenceMode ? ' (Reference Mode)' : ''}`;
    console.log(`Server running on port ${port}`);
  });

  return app;
};
