        console.log("new sync "+JSON.stringify(this.search));
        this._walker({ts:this, search:this.search, cb:callback, page:1, results:[]});
    }

    set(search) {
        this.search = search;
        lfs.writeObjectToFile(search.id+'.search',search);    
    }

