            return encodeURIComponent(param) + '=' + encodeURIComponent(parameters[param]);
        }).join('&');

        let signature = crypto.createHmac('sha512', this.secret).update(paramString).digest('hex');

        return {
            Key: this.key,
            Sign: signature
        };
