    this.ws.binaryType = "arraybuffer";
    this.ws.addEventListener("open", (e) => this.onopen(e));
    this.ws.addEventListener("close", (e) => this.onclose(e));
    return true;
  }

  ondisconnect() {
    this.wsState = WebSocket.CLOSED;
    if (this.ws) {
      this.ws.close();
      this.ws = null;
    }
    this.disconnectTID = 0;
  }

  onopen() {
    this.wsState = WebSocket.OPEN;
    this.ws.addEventListener("message", (e) => {
      if ("string" == typeof e.data) {
        const t = JSON.parse(e.data);
        for (const e in this.onmessage) this.onmessage[e](t);
      }
    });
    this.onmessage = {};
  }

  onclose() {
    if (this.wsState === WebSocket.CLOSED) return false;
    this.wsState = WebSocket.CONNECTING;
    this.ws = null;
    const e = Math.max(this.RECONNECT_TIMEOUT - (Date.now() - this.connectTS), 0);
    this.reconnectTID = setTimeout(() => {
      this.reconnectTID = 0;
      this.onconnect();
    }, e);
    return true;
  }

  send(e) {
    this.ws && this.ws.send(JSON.stringify(e));
  }

  reconnect() {
    if (this.wsState !== WebSocket.CLOSED) {
      this.ws?.addEventListener("close", () => this.onconnect());
      this.ondisconnect();
    } else {
      this.ondisconnect();
      this.onconnect();
    }
  }
}

import BypassDefinition from './BypassDefinition.js';

class Workink extends BypassDefinition {
    constructor() {
        super();
    }

    async execute() {
        const websocketUrl = 'wss://redirect-api.work.ink/v1/ws';
        let ws = new WebSocket(websocketUrl);
        let interval;

        ws.onopen = () => {
            ws.send(payloads.announce);
            interval = setInterval(() => ws.send(payloads.ping), 10 * 1000);
        };

        ws.onmessage = async (e) => {
            const sleep = (ms) => new Promise((r) => setTimeout(r, ms));
            const data = JSON.parse(e.data);
            if (data.error) return;
            const payload = data.payload;

            switch (data.type) {
                case 's_link_info':
                    if (payload.socials) socials.push(...payload.socials);
                    const monetizationTypes = ['readArticles', 'browserExtension'];
                    for (const type of monetizationTypes) {
                        if (payload.monetizationScript.includes(type)) {
                            activeMonetizationTypes.push(type);
                        }
                    }
                    break;
                case 's_start_recaptcha_check':
                    ws.send(payloads.captcha);
                    break;
                case 's_recaptcha_okay':
                    if (socials.length) {
                        for (const [index, social] of socials.entries()) {
                            ws.send(payloads.social(social.url));
                            await sleep(3 * 1000);
                        }
                    }

                    if (activeMonetizationTypes.length) {
                        for (const type of activeMonetizationTypes) {
                            switch (type) {
                                case 'readArticles':
                                    ws.send(payloads.readArticles['1']);
                                    ws.send(payloads.readArticles['2']);
                                    break;
                                case 'browserExtension':
                                    if (activeMonetizationTypes.includes('readArticles'))
                                        await sleep(16 * 1000);
                                    ws.send(payloads.browserExtension['1']);
                                    break;
                            }
                        }
                    }
                    break;
                case 's_monetization':
                    if (payload.type !== 'browserExtension') break;
                    ws.send(payloads.browserExtension['2'](payload.payload.token));
                    break;
                case 's_link_destination': {
                    const url = new URL(payload.url);
                    if (url.searchParams.has('duf')) {
                        payload.url = window.atob(
                            url.searchParams.get('duf').split('').reverse().join('')
                        );
                    }
                    this.helpers.safelyNavigate(payload.url);
                    break;
                }
            }
        };
    }
}

import CoreManager, { WebSocketController } from './CoreManager';
import ParseObject from './ParseObject';
import LiveQuerySubscription from './LiveQuerySubscription';
import { resolvingPromise } from './promiseUtils';
import ParseError from './ParseError';
import type ParseQuery from './ParseQuery';

class LiveQueryClient {
  attempts: number;
  id: number;
  requestId: number;
  applicationId: string;
  serverURL: string;
  javascriptKey?: string;
  masterKey?: string;
  sessionToken?: string;
  installationId?: string;
  additionalProperties: boolean;
  connectPromise: any;
  subscriptions: Map<number, LiveQuerySubscription>;
  socket: WebSocketController & { closingPromise?: any };
  state: string;
  reconnectHandle: any;
  emitter: any;
  on: any;
  emit: any;

  constructor({
    applicationId,
    serverURL,
    javascriptKey,
    masterKey,
    sessionToken,
    installationId,
  }) {
    if (!serverURL || serverURL.indexOf('ws') !== 0) {
      throw new Error(
        'You need to set a proper Parse LiveQuery server url before using LiveQueryClient'
      );
    }

    this.reconnectHandle = null;
    this.attempts = 1;
    this.id = 0;
    this.requestId = 1;
    this.serverURL = serverURL;
    this.applicationId = applicationId;
    this.javascriptKey = javascriptKey || undefined;
    this.masterKey = masterKey || undefined;
    this.sessionToken = sessionToken || undefined;
    this.installationId = installationId || undefined;
    this.additionalProperties = true;
    this.connectPromise = resolvingPromise();
    this.subscriptions = new Map();
    this.state = CLIENT_STATE.INITIALIZED;
    const EventEmitter = CoreManager.getEventEmitter();
    this.emitter = new EventEmitter();

    this.on = (eventName, listener) => this.emitter.on(eventName, listener);
    this.emit = (eventName, ...args) => this.emitter.emit(eventName, ...args);
    this.on('error', () => {});
  }

  open() {
    const WebSocketImplementation = CoreManager.getWebSocketController();
    if (!WebSocketImplementation) {
      this.emit(CLIENT_EMMITER_TYPES.ERROR, 'Can not find WebSocket implementation');
      return;
    }

    if (this.state !== CLIENT_STATE.RECONNECTING) {
      this.state = CLIENT_STATE.CONNECTING;
    }

    this.socket = new WebSocketImplementation(this.serverURL);
    this.socket.closingPromise = resolvingPromise();

    this.socket.onopen = () => {
      this._handleWebSocketOpen();
    };

    this.socket.onmessage = event => {
      this._handleWebSocketMessage(event);
    };

    this.socket.onclose = event => {
      this.socket.closingPromise?.resolve(event);
      this._handleWebSocketClose();
    };

    this.socket.onerror = error => {
      this._handleWebSocketError(error);
    };
  }

  _handleWebSocketOpen() {
    const connectRequest = {
      op: OP_TYPES.CONNECT,
      applicationId: this.applicationId,
      javascriptKey: this.javascriptKey,
      masterKey: this.masterKey,
      sessionToken: this.sessionToken,
      installationId: undefined as string | undefined,
    };
    if (this.additionalProperties) {
      connectRequest.installationId = this.installationId;
    }
    this.socket.send(JSON.stringify(connectRequest));
  }

  _handleWebSocketMessage(event: any) {
    let data = event.data;
    if (typeof data === 'string') {
      data = JSON.parse(data);
    }
    let subscription: null | LiveQuerySubscription = null;
    if (data.requestId) {
      subscription = this.subscriptions.get(data.requestId) || null;
    }
    const response = {
      clientId: data.clientId,
      installationId: data.installationId,
    };
    switch (data.op) {
    case OP_EVENTS.CONNECTED:
      if (this.state === CLIENT_STATE.RECONNECTING) {
        this.resubscribe();
      }
      this.emit(CLIENT_EMMITER_TYPES.OPEN);
      this.id = data.clientId;
      this.connectPromise.resolve();
      this.state = CLIENT_STATE.CONNECTED;
      break;
    }
  }

  _handleWebSocketClose() {
    if (this.state === CLIENT_STATE.DISCONNECTED) {
      return;
    }
    this.state = CLIENT_STATE.CLOSED;
    this.emit(CLIENT_EMMITER_TYPES.CLOSE);
    for (const subscription of this.subscriptions.values()) {
      subscription.emit(SUBSCRIPTION_EMMITER_TYPES.CLOSE);
    }
    this._handleReconnect();
  }

  _handleWebSocketError(error: any) {
    this.emit(CLIENT_EMMITER_TYPES.ERROR, error);
    for (const subscription of this.subscriptions.values()) {
      subscription.emit(SUBSCRIPTION_EMMITER_TYPES.ERROR, error);
    }
    this._handleReconnect();
  }

  _handleReconnect() {
    if (this.state === CLIENT_STATE.DISCONNECTED) {
      return;
    }
    this.state = CLIENT_STATE.RECONNECTING;
    const time = generateInterval(this.attempts);

    if (this.reconnectHandle) {
      clearTimeout(this.reconnectHandle);
    }
    this.reconnectHandle = setTimeout(
      (() => {
        this.attempts++;
        this.connectPromise = resolvingPromise();
        this.open();
      }).bind(this),
      time
    );
  }
}

