class BingChatWebSocket {
  private webSockets: Record<string, WebSocket | null> = {};

  async getSocketId(): Promise<string> {
    const socketUrl = 'wss://sydney.bing.com/sydney/ChatHub';
    return await new Promise((resolve, reject) => {
      try {
        const ws = new WebSocket(socketUrl);
        const socketId = uuidv4();

        ws.onopen = (_e) => {
          const hello = JSON.stringify({ protocol: 'json', version: 1 }) + '\x1e';
          ws.send(hello);
        };

        ws.onclose = () => {
          this.webSockets[socketId] = null;
        };

        ws.onerror = (e) => {
          if (e.type === 'error') {
            reject(new Error(`WebSocket \`${socketUrl}\` did not connect successfully.`));
            return;
          }
          reject(e);
        };

        ws.onmessage = (e) => {
          const msg = e.data;
          if (msg === '{}\x1e') {
            this.webSockets[socketId] = ws;
            resolve(socketId);
            return;
          }
          ws.close();
          this.webSockets[socketId] = null;
          reject(new Error('WebSocket did not connect successfully'));
        };
      } catch (e) {
        reject(e);
      }
    });
  }

  async ping(socketId: string): Promise<void> {
    return await new Promise((resolve, _reject) => {
      const ws = this.webSockets[socketId];
      if (ws == null) throw new Error(`WebSocket ${socketId} not found`);

      ws.send(JSON.stringify({ type: 6 }) + '\x1e');
      resolve(null);
    });
  }

  async send(
    socketId: string,
    msg: object,
    onMessage: (data: any) => void
  ): Promise<any> {
    return await new Promise((resolve, _reject) => {
      const ws = this.webSockets[socketId];
      if (ws == null) throw new Error(`WebSocket ${socketId} not found`);

      ws.onmessage = (e) => {
        const msg = e.data;
        for (const item of msg.split('\x1e').filter(Boolean)) {
          const data = JSON.parse(item.replaceAll('\n', '\\n'));
          onMessage(data);

          if (data.type === 2) {
            setTimeout(() => {
              resolve(data);
            });
          }
        }
      };
      ws.send(JSON.stringify(msg) + '\x1e');
    });
  }

  async close(socketId: string): Promise<void> {
    const ws = this.webSockets[socketId];
    ws?.close();
    this.webSockets[socketId] = null;
  }
}

