  const chunks = [];
  stream.on('data', (chunk) => {
    chunks.push(chunk);
  });
  stream.on('end', () => {
    const buffer = Buffer.concat(chunks);
    res.setHeader('Content-Type', 'image/jpeg');
    res.send(buffer);
  });
});

app.get('/buffer-arraybuffer', (req, res) => {
