    this.app = app;
  }

  handler(req, res, next) {
    if (this.app) {
      this.app(req, res, next);
    } else {
      next();
    }
  }

  livereload(req, res) {
    const filePath = path.join(this.options.livereloadPath || '.', 'livereload.js');
