```javascript
const http = require('http');
const fs = require('fs');
const path = require('path');
const { Transform } = require('stream');

const DEFAULT_PORT = 90;
const STATIC_DIRS = ['../src', '../dist'];
const JS_EXTENSIONS = ['.js'];
const BINARY_EXTENSIONS = ['.jpg', '.jpeg', '.png', '.gif', '.svg', '.pdf', '.ico', '.webp', '.zip', '.tar', '.gz', '.7z', '.bin', '.exe', '.dll', '.dmg'];

function createServer(port = DEFAULT_PORT) {
  const server = http.createServer((req, res) => {
    const url = req.url;
    const filePath = path.join(__dirname, url);

    if (url.startsWith('/concatenate/')) {
      const files = url.split('/').slice(2);
      concatenateFiles(files, res);
      return;
    }

    if (url.startsWith('/redirect/')) {
      const target = url.split('/').pop();
      res.writeHead(301, { Location: target });
      res.end();
      return;
    }

    if (STATIC_DIRS.some(dir => filePath.startsWith(dir))) {
      serveStaticFile(filePath, res);
    } else {
      res.writeHead(404, { 'Content-Type': 'text/plain' });
      res.end('404 Not Found');
    }
  });

  server.listen(port, () => {
    console.log(`Server running at http://127.0.0.1:${port}/`);
    console.log('Loaded files:');
    STATIC_DIRS.forEach(dir => {
      fs.readdir(dir, (err, files) => {
        if (err) {
          console.error(`Error reading directory ${dir}:`, err);
          return;
        }
        files.forEach(file => {
          console.log(`${dir}/${file}`);
        });
      });
    });
  });
}

function concatenateFiles(files, res) {
  const transform = new Transform({
    transform(chunk, encoding, callback) {
      this.push(chunk);
      callback();
    }
  });

  let first = true;
  files.forEach(file => {
    const filePath = path.join(__dirname, file);
    const stream = fs.createReadStream(filePath);
    if (!first) {
      stream.pipe(transform);
    } else {
      first = false;
      stream.pipe(res);
    }
  });

  if (files.length === 0) {
    res.writeHead(404, { 'Content-Type': 'text/plain' });
    res.end('404 Not Found');
  }
}

function serveStaticFile(filePath, res) {
  const ext = path.extname(filePath);
  const contentType = getContentType(ext);

  fs.readFile(filePath, (err, data) => {
    if (err) {
      res.writeHead(500, { 'Content-Type': 'text/plain' });
      res.end('500 Internal Server Error');
      return;
    }

    if (BINARY_EXTENSIONS.includes(ext)) {
      res.writeHead(200, { 'Content-Type': contentType });
      res.end(data);
    } else {
      res.writeHead(200, { 'Content-Type': contentType });
      res.end(data.toString());
    }
  });
}

function getContentType(ext) {
  switch (ext) {
    case '.html':
      return 'text/html';
    case '.css':
      return 'text/css';
    case '.js':
      return 'application/javascript';
    case '.json':
      return 'application/json';
    case '.txt':
      return 'text/plain';
    case '.xml':
      return 'application/xml';
    case '.pdf':
      return 'application/pdf';
    case '.jpg':
    case '.jpeg':
    case '.png':
    case '.gif':
    case '.svg':
    case '.ico':
    case '.webp':
      return 'image/' + ext.slice(1);
    case '.zip':
    case '.tar':
    case '.gz':
    case '.7z':
      return 'application/zip';
    case '.bin':
    case '.exe':
    case '.dll':
    case '.dmg':
      return 'application/octet-stream';
    default:
      return 'text/plain';
  }
}

createServer();
```

