const net = require('net');

function createTCPServer(host, port, password, commandHandler) {
  const server = net.createServer(socket => {
    const receive = {
      data: Buffer.from(''),
      socket,
    };
    socket.on('data', data => {
      receiveData(receive, data, password, commandHandler);
    });
    socket.on('error', (err) => {
      throw err;
    });
  }).on('error', (err) => {
    throw err;
  });

  server.listen({
    port,
    host,
  }, () => {
    console.log(`server listen on ${ host }:${ port }`);
  });

  return server;
}

function receiveData(receive, data, password, commandHandler) {
  receive.data = Buffer.concat([receive.data, data]);
  checkData(receive, password, commandHandler);
}

function checkData(receive, password, commandHandler) {
  const buffer = receive.data;
  let length = 0;
  let data;
  let code;
  if(buffer.length < 2) { return; }
  length = buffer[0] * 256 + buffer[1];
  if(buffer.length >= length + 2) {
    data = buffer.slice(2, length - 2);
    code = buffer.slice(length - 2);
    if(!checkCode(data, password, code)) {
      receive.socket.end(pack({ code: 2 }));
      return;
    }
    commandHandler(data, code).then(s => {
      receive.socket.end(pack({ code: 0, data: s }));
    }).catch(err => {
      let code = -1;
      if(err === 'invalid command') { code = 1; }
      receive.socket.end(pack({ code }));
    });
    if(buffer.length > length + 2) {
      checkData(receive, password, commandHandler);
    }
  }
}

function checkCode(data, password, code) {
  const time = Number.parseInt(data.slice(0, 6).toString('hex'), 16);
