const net = require('net');

class Server {
  constructor(options, origins) {
    this.origins = origins || [];
    this.port = options.port || 843;
    this.log = options.log || console.log;
    this.socket = null;
    this.server = options.server;
    this.buffer = null;
  }

  listen(port, server, cb) {
    this.socket = net.createServer((socket) => {
      this.responder(socket);
    });

    this.socket.on('error', (err) => {
      this.log('Server error:', err);
    });

    this.socket.listen(port, () => {
      this.log(`Server listening on port ${port}`);
      if (server) {
        server.listen(port, () => {
          cb(null, this);
        });
      } else {
        cb(null, this);
      }
    });
  }

  responder(socket) {
    let data = '';
    socket.on('data', (chunk) => {
      data += chunk.toString();
    });

    socket.on('end', () => {
      const request = data.split('\n')[0];
      const origin = request.split('/')[2];
      if (this.origins.includes(origin)) {
        socket.write(this.buffer);
      } else {
        socket.write('<cross-domain-policy><allow-access-from domain="*" secure="false"/></cross-domain-policy>');
