const dgram = require('dgram');

class MasterIPC {
  constructor(options) {
    this.options = options;
    this._sock = null;
    this.children = [];
    this.sock = null;
  }

  IPCSocket() {
    if (!this._sock) {
      this._sock = dgram.createSocket('udp4');
      this._sock.on('message', (msg, rinfo) => {
        const data = JSON.parse(msg.toString());
        this.invoke(data.method, data.args, data.worker);
      });
    }
    return this._sock;
  }

  setupIPC() {
    this.sock = this.IPCSocket();
    this.sock.bind(this.options.port, this.options.address, () => {
      this.sock.emit('start');
    });
  }

  call(id, method, ...args) {
    const data = JSON.stringify({ id, method, args });
    this.sock.send(data, 0, data.length, this.options.port, this.options.address);
  }

  invoke(method, args, worker) {
    if (typeof worker[method] === 'function') {
      worker[method](...args);
    }
  }

  connect(worker) {
    this.children.push(worker);
    this.sock.emit('worker connected', worker);
  }

  connectMaster(sig) {
    process.on('SIGTERM', () => {
      process.kill(process.pid, sig);
    });
    this.setupIPC();
  }

  masterKilled(data) {
    this.sock.emit('restart', data);
  }
}
