        const data = requests.map(req => this._formatData(req.method, req.params, req.id));
        this._handles = Object.assign({}, this._handles, requests.reduce((acc, req) => {
            acc[req.id] = { successCallback, errorCallback };
            return acc;
        }, {}));
        this._send({ jsonrpc: '2.0', method: 'batch', params: data });
