        this.init();
        this.wsClient = new WebSocket(rpcUrl, options.protocol);

        this.wsClient.onopen = () => {
            this.fireSendQueue();
            onOpenCallback();
        };

        this.wsClient.onclose = () => {
            onCloseCallback();
            this.planToReconnect(rpcUrl, options);
