const path = require('path');

async function downloadIcons(url, style, callback, breakOnError = false) {
  try {
    const response = await axios.get(url, {
      headers: {
        'Accept': 'application/json',
        'Authorization': 'Bearer your-token-here'
      }
    });

    const icons = response.data.icons;
    const processedIcons = [];
    const targetImagePath = path.join(__dirname, 'icons');

    if (!fs.existsSync(targetImagePath)) {
      fs.mkdirSync(targetImagePath);
    }

    const iconNames = new Set();

    icons.forEach(icon => {
      if (iconNames.has(icon.name)) {
        if (breakOnError) {
          throw new Error(`Duplicate icon name found: ${icon.name}`);
        }
        return;
      }
      iconNames.add(icon.name);

      const svgPath = path.join(targetImagePath, `${icon.name}.svg`);
      fs.writeFileSync(svgPath, icon.svg);

      processedIcons.push({
        name: icon.name,
        svg: icon.svg,
        category: icon.category,
        style: style,
        tags: icon.tags,
        code: icon.code,
        unicode: icon.unicode,
        pro: icon.pro
      });
    });

    callback(null, processedIcons.sort((a, b) => a.name.localeCompare(b.name)));
  } catch (error) {
    callback(error, null);
  }
}
