class AjaxTransport {
    constructor() {
        this.xhrId = 0;
        this.xhrCallbacks = {};
        this.xhrSuccessStatus = { 200: 200, 201: 200, 202: 200, 204: 204 };
        this.xhrSupported = XMLHttpRequest ? new XMLHttpRequest() : false;
    }

    createXHR() {
        if (this.xhrSupported) {
            return new XMLHttpRequest();
        }
        return undefined;
    }

    sendRequest(options, headers, complete) {
        const xhr = this.createXHR();
        if (!xhr) return;

        xhr.open(options.type, options.url, options.async);
        xhr.withCredentials = options.withCredentials || false;

        for (const [key, value] of Object.entries(headers)) {
            xhr.setRequestHeader(key, value);
        }

        xhr.onload = () => {
            if (xhr.status >= 200 && xhr.status < 300 || this.xhrSuccessStatus[xhr.status]) {
                complete(xhr.response, xhr.status, xhr);
            } else {
                complete(null, xhr.status, xhr);
            }
        };

        xhr.onerror = () => {
            complete(null, xhr.status, xhr);
        };

        xhr.ontimeout = () => {
            complete(null, xhr.status, xhr);
        };

        xhr.onabort = () => {
            complete(null, xhr.status, xhr);
        };

        xhr.xhrId = ++this.xhrId;
        this.xhrCallbacks[xhr.xhrId] = complete;

        xhr.send(options.data);
    }

    abortRequest(callback) {
        const xhrId = Object.keys(this.xhrCallbacks).find(id => this.xhrCallbacks[id] === callback);
        if (xhrId) {
            const xhr = this.xhrCallbacks[xhrId].xhr;
            if (xhr) {
                xhr.abort();
                delete this.xhrCallbacks[xhrId];
            }
        }
    }

    getTransport(options) {
        if (this.xhrSupported) {
            return {
                send: (headers, complete) => this.sendRequest(options, headers, complete),
                abort: callback => this.abortRequest(callback)
