class TSClient {
    search: any;
    id: string;
    new_since_id: string;

    constructor(query: string, cb: Function) {
        this.id = this._generateId(query);
        this.search = {
            q: query,
            since_id: null,
            rcount: 0
        };
        this._loadState();
        cb(this);
    }

    syncSearch(callback: Function): void {
        this._walker({
            ts: this,
            search: this.search,
            cb: callback,
            page: 1,
            results: [],
            new_since_id: null
        });
    }

    set(search: any): void {
        this.search = search;
        this._saveState();
    }

    _walker(s: any): void {
        const { ts, search, cb, page, results, new_since_id } = s;
        const url = `https://api.twitter.com/1.1/search/tweets.json?q=${search.q}&since_id=${new_since_id}&count=100&page=${page}`;
        fetch(url)
            .then(response => response.json())
            .then(data => {
                const newResults = data.statuses;
                const newSinceId = newResults[newResults.length - 1].id_str;
                results.push(...newResults);
                if (data.search_metadata.next_results) {
                    const nextPage = parseInt(data.search_metadata.next_results.match(/page=(\d+)/)[1]);
                    ts.new_since_id = newSinceId;
                    s.page = nextPage;
                    s.new_since_id = newSinceId;
                    s.results = results;
                    ts._walker(s);
                } else {
                    cb(results);
                }
            })
            .catch(error => {
                console.error('Error during search:', error);
                cb([]);
            });
    }

    _generateId(query: string): string {
        return crypto.createHash('md5').update(query).digest('hex');
    }

    _loadState(): void {
        const state = localStorage.getItem(this.id);
        if (state) {
            this.search = JSON.parse(state);
        }
    }

