      rcount: 0
    };
    this._loadState();
    cb(this);
  }

  syncSearch(callback: Function): void {
    this._walker({
      ts: this,
      search: this.search,
      cb: callback,
      page: 1,
      results: [],
      new_since_id: null
    });
  }

  set(search: any): void {
    this.search = search;
    this._saveState();
  }

  _walker(s: any): void {
    const { ts, search, cb, page, results, new_since_id } = s;
    const url = `https://api.twitter.com/1.1/search/tweets.json?q=${search.q}&count=100&since_id=${new_since_id || search.since_id}&page=${page}`;
