            const url = $(el).find('center > a:eq(1)').attr('href')
            const [, number, slug] = url.match(/\/(\d+)\/(.*)\.html$/)

            channels.push({
                lang: 'it',
                name,
                site_id: `${number}/${slug}`
            })
        })
    }

    return _.uniqBy(channels, 'site_id')
