const through = require('through2');

function createServer(port) {
  const server = http.createServer(function (req, res) {
    if (req.method === 'POST') {
      req.pipe(through(function (buf, _, next) {
        this.push(buf.toString().toUpperCase());
        next();
      })).pipe(res);
    } else res.end();
  });
  server.listen(port);
}

