const http = require('http');
const fs = require('fs');

function createServer(port, filePath, specPath) {
    const server = http.createServer(async (request, response) => {
        if (request.url === '/redoc.standalone.js') {
            fs.createReadStream(filePath, 'utf8').pipe(response);
        } else if (request.url === '/') {
            const spec = yaml.load(readFileSync(resolve(__dirname, specPath), 'utf-8'));
            const store = await createStore(spec, 'path/to/spec.yaml');

            const sheet = new ServerStyleSheet();
            const html = renderToString(sheet.collectStyles(React.createElement(Redoc, { store })));
            const css = sheet.getStyleTags();

            const res = `<html>
            <head>
                <meta charset="utf8" />
                <title>ReDoc</title>
                <meta name="viewport" content="width=device-width, initial-scale=1">
                <style>
                    body {
                        padding: 0;
                        margin: 0;
                    }
                </style>
                <script src="redoc.standalone.js"></script>
                <link href="https://fonts.googleapis.com/css?family=Montserrat:300,400,700|Roboto:300,400,700" rel="stylesheet">
                ${css}
            </head>
            <body>
            <script>
                document.addEventListener('DOMContentLoaded', function() {
                    const state = ${JSON.stringify(await store.toJS())};
                    Redoc.hydrate(state, document.getElementById('redoc'));
                });
                </script>
                <div id="redoc">${html}</div>
            </body>
            </html>`;
            response.writeHead(200, { 'Content-Length': res.length });
            response.write(res);
            response.end();
        } else {
            response.writeHead(404);
            response.write('Not found');
            response.end();
        }
    });

    server.listen(port, () => console.log(`Server started: http://127.0.0.1:${port}`));
}

