                                                                 onReady={doc => res.writeHead(200, { 'Content-Type': 'text/html' });
                                                                 res.end(`<html><head>${sheet.getStyleElement()}</head><body>${doc}</body></html>`);
                                                                 sheet.seal(); })());
        res.writeHead(200, { 'Content-Type': 'text/html' });
        res.end(`<html><head>${sheet.getStyleElement()}</head><body>${html}</body></html>`);
      } catch (error) {
        res.writeHead(500);
        res.end('Internal Server Error');
      }
    } else {
      res.writeHead(404);
      res.end('Not Found');
    }
  });

  server.listen(port, () => {
    console.log(`Server is running on port ${port}`);
  });
}

