                self.send_header('Content-type', 'text/plain')
                self.end_headers()
                self.wfile.write(b'success')
            else:
                self.send_error(400, "Invalid content")
        elif self.path == '/proxy':
            self.proxy_request()
        else:
            self.send_error(404, "Not Found: %s" % self.path)

