```javascript
const http = require('http');
const fs = require('fs');
const path = require('path');
const { Transform } = require('stream');

const DEFAULT_PORT = 90;
const STATIC_DIRS = ['../src', '../dist'];
const JS_EXTENSIONS = ['.js'];
const BINARY_EXTENSIONS = ['.jpg', '.jpeg', '.png', '.gif', '.svg', '.pdf', '.ico', '.webp', '.zip', '.tar', '.gz', '.7z', '.bin', '.exe', '.dll'];

function createServer(port = DEFAULT_PORT) {
  const server = http.createServer((req, res) => {
    const url = req.url;
    const filePath = path.join(__dirname, url);

    if (url.startsWith('/concatenate/')) {
      const files = url.split('/').slice(2);
      concatenateFiles(files, res);
    } else {
      serveFile(filePath, req, res);
    }
  });

  server.listen(port, '127.0.0.1', () => {
    console.log(`Server running at http://127.0.0.1:${port}/`);
    loadStaticFiles();
  });

  return server;
}

function loadStaticFiles() {
  console.log('Loading static files...');
  STATIC_DIRS.forEach(dir => {
    fs.readdirSync(dir).forEach(file => {
      const filePath = path.join(dir, file);
      console.log(`Loaded static file: ${filePath}`);
    });
  });
}

function serveFile(filePath, req, res) {
  if (!fs.existsSync(filePath)) {
    res.writeHead(404, { 'Content-Type': 'text/plain' });
    res.end('404 Not Found');
    return;
  }

  const stats = fs.statSync(filePath);
  if (stats.isDirectory()) {
    res.writeHead(302, { 'Location': `${filePath}/` });
    res.end();
    return;
  }

  const contentType = getContentType(filePath);
  res.writeHead(200, { 'Content-Type': contentType });
  const fileStream = fs.createReadStream(filePath);
  fileStream.pipe(res);
}

function concatenateFiles(files, res) {
  const transform = new Transform({
    transform(chunk, encoding, callback) {
      this.push(chunk);
      callback();
    },
    flush(callback) {
      const concatenated = Buffer.concat(this._chunks).toString();
      res.writeHead(200, { 'Content-Type': 'application/javascript' });
      res.end(concatenated);
      callback();
    }
  });

  transform._chunks = [];

  files.forEach(file => {
    const filePath = path.join(__dirname, file);
    const fileStream = fs.createReadStream(filePath);
    fileStream.on('data', chunk => {
      transform.push(chunk);
    });
    fileStream.on('end', () => {
      transform.push(null);
    });
  });
}

function getContentType(filePath) {
  const ext = path.extname(filePath).toLowerCase();
  if (JS_EXTENSIONS.includes(ext)) {
    return 'application/javascript';
  } else if (BINARY_EXTENSIONS.includes(ext)) {
    return 'application/octet-stream';
  } else {
    const mime = require('mime-types');
    return mime.lookup(filePath) || 'text/plain';
  }
}

module.exports = createServer;
