const http = require('http');
const fs = require('fs');
const path = require('path');
const { pipeline } = require('stream');

const DEFAULT_PORT = 90;
const SERVER_HOST = '127.0.0.1';

const staticFiles = new Map();
const binaryExtensions = ['.jpg', '.jpeg', '.png', '.gif', '.svg', '.ico', '.webp', '.pdf', '.mp4', '.mp3', '.wav', '.avi', '.mov', '.flv', '.swf', '.zip', '.tar', '.gz', '.7z', '.bin', '.exe', '.dll', '.dmg', '.iso', '.jar', '.war', '.ear', '.sar', '.apk', '.msi', '.bat', '.sh', '.cmd', '.class', '.jar', '.war', '.ear', '.sar', '.apk', '.msi', '.bat', '.sh', '.cmd'];

function createServer(port = DEFAULT_PORT) {
  const server = http.createServer((req, res) => {
    const url = req.url;
    const filePath = path.join(__dirname, url);

    if (url.startsWith('/static/')) {
      const staticPath = url.substring(8);
      const file = staticFiles.get(staticPath);
      if (file) {
        res.writeHead(200, { 'Content-Type': file.type });
        pipeline(file.stream, res, (err) => {
          if (err) {
            console.error('Pipeline failed', err);
          } else {
            console.log(`Served static file: ${staticPath}`);
          }
        });
        return;
      }
    }

    if (url.startsWith('/concat/')) {
      const concatPath = url.substring(8);
      const files = concatPath.split(',');
      const concatenatedStream = fs.createWriteStream(path.join(__dirname, 'concat.js'));
      files.forEach((file) => {
        const filePath = path.join(__dirname, file);
        const fileStream = fs.createReadStream(filePath);
        fileStream.pipe(concatenatedStream, { end: false });
      });
      concatenatedStream.end();
      concatenatedStream.on('finish', () => {
        res.writeHead(200, { 'Content-Type': 'application/javascript' });
        concatenatedStream.pipe(res);
        console.log(`Concatenated files: ${files.join(', ')}`);
      });
      return;
    }

    if (url.startsWith('/binary/')) {
      const binaryPath = url.substring(8);
      const fileStream = fs.createReadStream(path.join(__dirname, binaryPath));
      res.writeHead(200, { 'Content-Type': 'application/octet-stream' });
      pipeline(fileStream, res, (err) => {
        if (err) {
          console.error('Pipeline failed', err);
        } else {
          console.log(`Served binary file: ${binaryPath}`);
        }
      });
      return;
    }

    if (url.startsWith('/redirect/')) {
      const redirectPath = url.substring(10);
      res.writeHead(301, { Location: redirectPath });
      res.end();
      return;
    }

    const contentType = getContentType(filePath);
    if (contentType) {
      fs.readFile(filePath, (err, data) => {
        if (err) {
          res.writeHead(404);
          res.end('File not found');
        } else {
          res.writeHead(200, { 'Content-Type': contentType });
          res.end(data);
        }
      });
    } else {
      res.writeHead(404);
      res.end('File not found');
    }
  });

  server.listen(port, SERVER_HOST, () => {
    console.log(`Server listening on http://${SERVER_HOST}:${port}`);
    loadStaticFiles();
  });

  function loadStaticFiles() {
    const srcDir = path.join(__dirname, '../src');
    const distDir = path.join(__dirname, '../dist');
    fs.readdir(srcDir, (err, files) => {
      if (err) {
        console.error('Error reading src directory', err);
        return;
      }
      files.forEach((file) => {
        const filePath = path.join(srcDir, file);
        const contentType = getContentType(filePath);
        const stream = fs.createReadStream(filePath);
        staticFiles.set(file, { path: filePath, type: contentType, stream });
        console.log(`Loaded static file: ${file}`);
      });
      fs.readdir(distDir, (err, files) => {
        if (err) {
          console.error('Error reading dist directory', err);
          return;
        }
        files.forEach((file) => {
          const filePath = path.join(distDir, file);
          const contentType = getContentType(filePath);
          const stream = fs.createReadStream(filePath);
          staticFiles.set(file, { path: filePath, type: contentType, stream });
          console.log(`Loaded static file: ${file}`);
        });
      });
    });
  }

  function getContentType(filePath) {
    const ext = path.extname(filePath).toLowerCase();
    if (binaryExtensions.includes(ext)) {
      return 'application/octet-stream';
    }
    switch (ext) {
      case '.html':
        return 'text/html';
      case '.css':
        return 'text/css';
      case '.js':
        return 'application/javascript';
      case '.json':
        return 'application/json';
      case '.txt':
        return 'text/plain';
      case '.xml':
        return 'application/xml';
      case '.pdf':
        return 'application/pdf';
      case '.png':
        return 'image/png';
      case '.jpg':
        return 'image/jpeg';
      case '.jpeg':
        return 'image/jpeg';
      case '.gif':
        return 'image/gif';
      case '.svg':
        return 'image/svg+xml';
      default:
        return null;
    }
  }
}

createServer(process.argv[2] ? parseInt(process.argv[2], 10) : DEFAULT_PORT);
