const net = require('net');
const { Transform } = require('stream');

function createTCPServer() {
  const server = net.createServer();
  const parser = new Transform({ readableObjectMode: true });
  let dataEvents = 0;
  const maxDataEvents = 10;

  parser._transform = function(chunk, encoding, callback) {
    try {
      const data = JSON.parse(chunk.toString());
      this.push(data);
      dataEvents++;
      if (dataEvents >= maxDataEvents || this.readableEnded) {
        server.close();
      }
      callback();
    } catch (error) {
      callback(error);
    }
  };

  server.on('connection', (socket) => {
    socket.pipe(parser);
    parser.on('end', () => {
      server.close();
    });
  });

  server.listen(9999);
}

