var net = require('net');

class Server {
  constructor(options, origins) {
    this.origins = origins || ['*:*'];
    this.port = 843;
    this.log = console.log;

    Object.keys(options).forEach((key) => {
      this[key] && (this[key] = options[key]);
    });

    this.socket = net.createServer((socket) => {
      socket.on('error', () => {
        this.responder.call(this, socket);
      });
      this.responder.call(this, socket);
    });

    this.socket.on('error', (err) => {
      if (err.errno == 13) {
        this.log && this.log(
          'Unable to listen to port `' + this.port + '` as your Node.js instance does not have root privileges. ' +
          (
            this.server
            ? 'The Flash Policy File requests will only be served inline over the supplied HTTP server. Inline serving is slower than a dedicated server instance.'
            : 'No fallback server supplied, we will be unable to answer Flash Policy File requests.'
          )
        );
        this.emit('connect_failed', err);
        this.socket.removeAllListeners();
        delete this.socket;
      } else {
        this.log && this.log('FlashPolicyFileServer received an error event:\n' + (err.message ? err.message : err));
      }
    });

    this.socket.on('timeout', () => {});
    this.socket.on('close', (err) => {
      err && this.log && this.log('Server closing due to an error: \n' + (err.message ? err.message : err));
      if (this.server && this.server['@'] && this.server.online) {
        this.server.removeListener('connection', this.server['@']);
      }
      delete this.server?.online;
    });

    this.compile();
  }

  listen(port, server, cb) {
    let callback;
    const args = Array.prototype.slice.call(arguments, 0);

    args.forEach((arg) => {
      const type = typeof arg;
      if (type === 'number') this.port = arg;
      if (type === 'function') callback = arg;
      if (type === 'object') this.server = arg;
    });

    if (this.server) {
      this.server['@'] = (socket) => {
        socket.once('data', (data) => {
          if (
            data &&
            data[0] === 60 &&
            data.toString() === '<policy-file-request/>\0' &&
            socket &&
            (socket.readyState === 'open' || socket.readyState === 'writeOnly')
          ) {
            try {
              socket.end(this.buffer);
            } catch (e) {}
          }
        });
      };
      this.server.on('connection', this.server['@']);
    }

    if (this.port >= 0) {
      this.socket.listen(this.port, () => {
        this.socket.online = true;
        callback?.call(this);
      });
    }

    return this;
  }

  responder(socket) {
    if (socket && socket.readyState == 'open' && socket.end) {
      try {
        socket.end(this.buffer);
      } catch (e) {}
    }
  }

  compile() {
    const xml = [
      '<?xml version="1.0"?>',
      '<!DOCTYPE cross-domain-policy SYSTEM "http://www.macromedia.com/xml/dtds/cross-domain-policy.dtd">',
      '<cross-domain-policy>'
    ];

    this.origins.forEach((origin) => {
      const parts = origin.split(':');
      xml.push('<allow-access-from domain="' + parts[0] + '" to-ports="'+ parts[1] +'"/>');
    });

    xml.push('</cross-domain-policy>');
    this.buffer = Buffer.from(xml.join(''), 'utf8');
    return this;
  }

  add() {
    const args = Array.prototype.slice.call(arguments, 0);
    let i = args.length;

    while (i--) {
      if (this.origins.indexOf(args[i]) >= 0) {
        args[i] = null;
      }
    }

    Array.prototype.push.apply(
      this.origins,
      args.filter((value) => !!value)
    );

    this.compile();
    return this;
  }

  remove(origin) {
    const position = this.origins.indexOf(origin);
    if (position > 0) {
      this.origins.splice(position,1);
      this.compile();
    }
    return this;
  }

  close() {
    this.socket.removeAllListeners();
    this.socket.close();
    return this;
  }
}

