```javascript
const net = require('net');

class Socket extends require('events').EventEmitter {
    constructor() {
        super();
        this.server = null;
        this.socks = [];
        this.settings = {};
        this.type = 'server';
    }

    bind(port, host, fn) {
        if (this.server) {
            this.server.close();
        }
        this.server = net.createServer();
        this.server.listen(port, host, fn);
        return this;
    }

    onconnect(sock) {
        this.emit('connect', sock);
    }

    addSocket(sock) {
        this.socks.push(sock);
        sock.on('data', (data) => {
            this.emit('data', sock, data);
        });
        sock.on('end', () => {
            this.removeSocket(sock);
            this.emit('end', sock);
        });
        sock.on('error', (err) => {
            this.handleErrors(sock);
        });
    }

    removeSocket(sock) {
        const index = this.socks.indexOf(sock);
        if (index !== -1) {
            this.socks.splice(index, 1);
        }
    }

    handleErrors(sock) {
        sock.destroy();
        this.emit('error', sock);
    }

    closeServer(fn) {
        this.server.close(() => {
            this.socks.forEach(sock => sock.destroy());
            fn();
        });
    }
}
