function makeTerminatingBackend(byte) {
  const { createServer } = require('net');

  const server = createServer((socket) => {
    socket.on('data', (buff) => {
      const code = buff.readInt32BE(4);
      if (code === 80877103 || code === 80877104) {
        const packet = Buffer.from(byte, 'utf-8');
        socket.write(packet);
      }
    });
    socket.on('close', () => {
      server.close();
    });
  });

  server.listen();
  const { port } = server.address();
  return port;
}

const net = require('net');
const JsonSocket = require('json-socket');

class P2PServer {
    constructor(send = () => {}) {
        this.tcpServer = net.createServer();
        this.tcpServer.maxConnections = config.p2pConnections * 2;
        this.clients = [];
        this.send = send;

        this.tcpServer.on('connection', (socket) => {
            if(!config.p2p) {
                socket.destroy();
                return;
            }

            this.tcpServer.getConnections((err, con) => {
                logT('p2p', 'server connected', con, 'max', this.tcpServer.maxConnections);
            });

            socket = new JsonSocket(socket);
            this.clients.push(socket);

            socket.on('close', () => {
                this.clients.splice(this.clients.indexOf(socket), 1);
            });

            socket.on('error', (err) => {});

            socket.on('message', (message) => {
                if(message.type && this.messageHandlers[message.type]) {
                    if(message.type != 'protocol' && !socket.rats) return;

                    this.messageHandlers[message.type](message.data, (data) => {
                        socket.sendMessage({
                            id: message.id,
                            data
                        });
                    }, socket, {
                        version: message.version,
                        info: message.info
                    });
                }
            });

            socket.protocolTimeout = setTimeout(() => socket._socket.destroy(), 7000);
        });
    }

    listen(port) {
        this.tcpServer.listen(port, '0.0.0.0');
    }

    close() {
        const promise = new Promise(resolve => this.tcpServer.close(resolve));
        for (const client in this.clients) {
            this.clients[client]._socket.destroy();
        }
        return promise;
    }

    on(type, callback) {
        this.messageHandlers[type] = callback;
    }
}

