const net = require('net');

class Node {
  constructor(id) {
    this.id_ = id;
    this.peers_ = {};
    this.PORT = 3000;
    this.server_ = net.createServer((socket) => {
      socket.on('data', (data) => {
        this.on_data(data, socket);
      });
      socket.on('end', () => {
        this.remove_peer(socket);
      });
      socket.on('error', (err) => {
        console.error('Socket error:', err);
        this.remove_peer(socket);
      });
    });
    this.server_.listen(this.PORT + this.id_);
  }

  async start() {
    try {
      const peer_ids = Array.from({ length: 5 }, () => Math.floor(Math.random() * 100));
      for (const peer_id of peer_ids) {
        await this.connect_to_peer(peer_id);
      }
    } catch (error) {
      console.error('Error connecting to peers:', error);
    }
  }

  on_data(data, socket) {
    const message = JSON.parse(data.toString());
    if (message.type === 'peer-discovery') {
      this.add_peer(socket, message.sender_id);
    } else if (message.type === 'message') {
      this.emit('message', message.data, message.sender_id);
    }
  }

  send(socket, data) {
    const message = JSON.stringify({ type: 'message', data, sender_id: this.id_ });
    socket.write(message);
  }

  broadcast(data) {
    for (const peer_id in this.peers_) {
      this.send(this.peers_[peer_id], data);
    }
  }

  add_peer(socket, remote_id) {
    this.peers_[remote_id] = socket;
    this.emit('peer-joined', remote_id);
  }

  remove_peer(socket) {
    for (const peer_id in this.peers_) {
      if (this.peers_[peer_id] === socket) {
        delete this.peers_[peer_id];
        this.emit('peer-left', peer_id);
        break;
      }
    }
  }

  list_peers() {
    return Object.keys(this.peers_);
  }

  connect_to_peer(peer_id) {
    return new Promise((resolve, reject) => {
      const socket = net.connect(this.PORT + peer_id, () => {
        this.add_peer(socket, peer_id);
        resolve(socket);
      });
      socket.on('error', (err) => {
        socket.destroy();
        reject(err);
      });
    });
  }
}
