        this.remove_peer(socket);
      });
      socket.on('error', (err) => {
        this.remove_peer(socket);
      });
    });
    this.server_.listen(this.PORT + this.id_);
  }

  async start() {
    const peer_ids = Array.from({length: 5}, () => Math.floor(Math.random() * 100));
    for (const peer_id of peer_ids) {
      await this.connect_to_peer(peer_id);
    }
  }

