    if (message.type === 'broadcast') {
      this.broadcast(message.data);
    } else if (message.type === 'peer') {
      this.add_peer(socket, message.peer_id);
    }
  }

  send(socket, data) {
    socket.write(JSON.stringify({ type: 'broadcast', data }));
  }

  broadcast(data) {
    for (const peer_id in this.peers_) {
      this.send(this.peers_[peer_id], data);
    }
  }

  add_peer(socket, remote_id) {
    this.peers_[remote_id] = socket;
  }

