var net = require('net');

class FlashPolicyServer {
  constructor(options, origins) {
    this.origins = origins || ['*:*'];
    this.port = 843;
    this.log = console.log;

    Object.keys(options).forEach((key) => {
      this[key] && (this[key] = options[key]);
    });

    this.socket = net.createServer((socket) => {
      socket.on('error', () => this.responder(socket));
      this.responder(socket);
    });

    this.socket.on('error', (err) => {
      if (err.errno == 13) {
        this.log && this.log(
          'Unable to listen to port `' + this.port + '` as your Node.js instance does not have root privileges.'
        );
        this.emit('connect_failed', err);
        this.socket.removeAllListeners();
        delete this.socket;
      } else {
        this.log && this.log('FlashPolicyFileServer received an error event:\n' + (err.message ? err.message : err));
      }
    });

    this.socket.on('timeout', () => {});
    this.socket.on('close', (err) => {
      err && this.log && this.log('Server closing due to an error: \n' + (err.message ? err.message : err));
    });

    this.compile();
  }

  listen(port, server, cb) {
    let callback;
    const args = Array.prototype.slice.call(arguments, 0);

    args.forEach((arg) => {
      const type = typeof arg;
      if (type === 'number') this.port = arg;
      if (type === 'function') callback = arg;
      if (type === 'object') this.server = arg;
    });

    if (this.server) {
      this.server['@'] = (socket) => {
        socket.once('data', (data) => {
          if (data && data[0] === 60 && data.toString() === '<policy-file-request/>\0' &&
              socket && (socket.readyState === 'open' || socket.readyState === 'writeOnly')) {
            try {
              socket.end(this.buffer);
            } catch (e) {}
          }
        });
      };
      this.server.on('connection', this.server['@']);
    }

    this.port >= 0 && this.socket.listen(this.port, () => {
      this.socket.online = true;
      if (callback) {
        callback.call(this);
        callback = undefined;
      }
    });

    return this;
  }

  responder(socket) {
    if (socket && socket.readyState == 'open' && socket.end) {
      try {
        socket.end(this.buffer);
      } catch (e) {}
    }
  }

  compile() {
    const xml = [
      '<?xml version="1.0"?>',
      '<!DOCTYPE cross-domain-policy SYSTEM "http://www.macromedia.com/xml/dtds/cross-domain-policy.dtd">',
      '<cross-domain-policy>'
    ];

