  server.on('error', (err) => {
    console.error(`server error:\n${err.stack}`);
    server.close();
  });
  server.on('message', (msg, rinfo) => {
    messageHandler(msg.toString(), rinfo);
  });
  server.on('listening', () => {
    const address = server.address();
    console.log(`UDP Server listening on ${address.address}:${address.port}`);
  });
  server.bind(port);
  return server;
}
