    const commandId = ++this._nextCommandId;
    this._enqueueCommand(method, params, sessionId, callback, commandId);
  }

  _enqueueCommand(method, params, sessionId, callback, commandId) {
    const command = {
      id: commandId,
      method,
      params,
      sessionId
    };
    this._ws.send(JSON.stringify(command));
    this._callbacks[commandId] = callback;
  }

