    this.book.sequence = 0;
    this.book.bids = [];
    this.book.asks = [];
    this.queue = [];
  }

  connect(): void {
    if (this.state === STATES.CONNECTED || this.state === STATES.CONNECTING) return;
    this.state = STATES.CONNECTING;
    this.socket = new WebSocket(this.websocketURI);
    this.socket.onopen = this.onOpen.bind(this);
    this.socket.onclose = this.onClose.bind(this);
    this.socket.onmessage = this.onMessage.bind(this);
    this.socket.onerror = (error) => {
      this.emit('error', error);
      this.changeState(STATES.ERROR);
    };
  }

  disconnect(): void {
    if (this.state !== STATES.CONNECTED) return;
    this.socket.close();
  }

  changeState(stateName: string): void {
    const state = STATES[stateName.toUpperCase()];
    if (state === undefined) return;
    this.state = state;
    this.emit('statechange', state);
  }

  onOpen(): void {
    this.changeState(STATES.CONNECTED);
    this.sync();
  }

  onClose(): void {
    this.changeState(STATES.DISCONNECTED);
    this.fail_count++;
    if (this.fail_count < 3) {
      setTimeout(() => this.connect(), 1000 * this.fail_count);
    }
  }

  onMessage(datastr: string): void {
    this.queue.push(datastr);
    if (this.state === STATES.SYNCHRONIZED) {
      this.processMessage(JSON.parse(datastr), new Date());
    }
  }

  sync(): void {
