      .then(data => {
        this.book.sequence = data.sequence;
        this.book.bids = data.bids;
        this.book.asks = data.asks;
        this.changeState(STATES.SYNCHRONIZED);
      })
      .catch(error => {
        this.emit('error', error);
        this.changeState(STATES.ERROR);
      });
  }

  processMessage(message: object, t: Date): void {
    if (message.type === 'update') {
      this.emit('update', message, t);
    } else if (message.type === 'add') {
      this.emit('add', message, t);
    } else if (message.type === 'remove') {
      this.emit('remove', message, t);
    }
  }
