const EventEmitter = require('events');

class Chrome extends EventEmitter {
  constructor(options, notifier) {
    super();
    this.host = options.host || 'localhost';
    this.port = options.port || 9222;
    this.secure = options.secure || false;
    this._notifier = notifier;
    this._callbacks = {};
    this._nextCommandId = 0;
    this.webSocketUrl = this._createWebSocketUrl();
    this._ws = null;
  }

  _connectToWebSocket() {
    return new Promise((resolve, reject) => {
      this._ws = new WebSocket(this.webSocketUrl);
      this._ws.onopen = () => {
        this.emit('connected');
        resolve();
      };
      this._ws.onerror = (error) => {
        this.emit('error', error);
        reject(error);
      };
      this._ws.onmessage = (event) => {
        this._handleMessage(JSON.parse(event.data));
      };
    });
  }

  _handleMessage(message) {
    if (message.id) {
      const callback = this._callbacks[message.id];
      if (callback) {
        callback(message.result, message.error);
        delete this._callbacks[message.id];
      }
    } else {
      this.emit(message.method, message.params);
    }
  }

  _enqueueCommand(method, params, sessionId, callback) {
    const commandId = ++this._nextCommandId;
    const command = { id: commandId, method, params, sessionId };
    this._ws.send(JSON.stringify(command));
    this._callbacks[commandId] = callback;
  }

  send(method, ...args) {
    return new Promise((resolve, reject) => {
      this._enqueueCommand(method, args, null, (result, error) => {
        if (error) {
          reject(error);
        } else {
          resolve(result);
        }
      });
    });
  }

  _createWebSocketUrl() {
    const protocol = this.secure ? 'wss' : 'ws';
    return `${protocol}://${this.host}:${this.port}`;
  }
}
