        let parameters = {
            command: 'withdraw',
            currency: currency,
            amount: amount,
            address: address
        };
        return this._private(parameters, callback);
    }

    _joinCurrencies(currencyA, currencyB) {
        return currencyA + '_' + currencyB;
    }
