    }

    _getPrivateHeaders(parameters) {
        const timestamp = Math.floor(Date.now() / 1000);
        const nonce = Math.floor(Math.random() * 1000000000);
        const parametersWithNonce = Object.assign({}, parameters, { nonce, timestamp });
        const parametersString = Object.keys(parametersWithNonce).sort().map(key => `${key}=${encodeURIComponent(parametersWithNonce[key])}`).join('&');
        const signature = CryptoJS.HmacSHA512(parametersString + this.secret, this.key).toString();
        return {
            'API-Key': this.key,
            'Sign': signature,
            'Nonce': nonce,
            'Timestamp': timestamp,
            'User-Agent': this.USER_AGENT
        };
    }

    _request(options, callback) {
        const xhr = new XMLHttpRequest();
        xhr.onreadystatechange = () => {
            if (xhr.readyState === 4) {
                if (xhr.status >= 200 && xhr.status < 300) {
                    callback(null, JSON.parse(xhr.responseText));
                } else {
                    callback(new Error(xhr.statusText), null);
                }
            }
        };
        xhr.open(options.method, options.url, true);
        xhr.setRequestHeader('User-Agent', this.USER_AGENT);
        if (options.headers) {
            Object.keys(options.headers).forEach(key => {
                xhr.setRequestHeader(key, options.headers[key]);
            });
        }
        xhr.send(options.body ? JSON.stringify(options.body) : null);
    }

    _public(parameters, callback) {
        const url = this.PUBLIC_API_URL + '?' + Object.keys(parameters).map(key => `${key}=${encodeURIComponent(parameters[key])}`).join('&');
        this._request({ url, method: 'GET' }, callback);
    }

    _private(parameters, callback) {
        const headers = this._getPrivateHeaders(parameters);
        const url = this.PRIVATE_API_URL + '?' + Object.keys(parameters).map(key => `${key}=${encodeURIComponent(parameters[key])}`).join('&');
        this._request({ url, method: 'GET', headers }, callback);
    }

    getTicker(callback) {
        this._public({ method: 'getmarketdata' }, callback);
    }

    getOrderBook(currencyA, currencyB, callback) {
        this._public({ method: 'getmarketorderbook', marketid: this._joinCurrencies(currencyA, currencyB) }, callback);
    }

    getTradeHistory(currencyA, currencyB, callback) {
        this._public({ method: 'getmarkettrades', marketid: this._joinCurrencies(currencyA, currencyB) }, callback);
