import os
import zipfile
import requests
import shutil
import tempfile

class Binary:
    def __init__(self, url, name=None, installDirectory=None):
        self.url = url
        self.name = name if name else "binary"
        self.installDirectory = installDirectory if installDirectory else os.path.join(os.getcwd(), "bin")
        self.binaryDirectory = None
        self.binaryPath = None

    def install(self):
        return self._getInstallDirectory().then(self._download).then(self._extract).then(self._cleanup)

    def _getInstallDirectory(self):
        if not os.path.exists(self.installDirectory):
            os.makedirs(self.installDirectory)
        return Promise.resolve(self.installDirectory)

    def _download(self, installDirectory):
        tempDir = tempfile.mkdtemp()
        response = requests.get(self.url, stream=True)
        if response.status_code == 200:
            zipPath = os.path.join(tempDir, self.name + ".zip")
            with open(zipPath, 'wb') as f:
                for chunk in response.iter_content(chunk_size=8192):
                    f.write(chunk)
            self.binaryDirectory = os.path.join(installDirectory, self.name)
            return Promise.resolve(zipPath)
        else:
            return Promise.reject("Failed to download binary package.")

    def _extract(self, zipPath):
        with zipfile.ZipFile(zipPath, 'r') as zip_ref:
            zip_ref.extractall(self.binaryDirectory)
        self.binaryPath = os.path.join(self.binaryDirectory, self.name)
        return Promise.resolve(self.binaryPath)

    def _cleanup(self, binaryPath):
        shutil.rmtree(tempfile.mkdtemp())
        return Promise.resolve("Installation complete.")
