            os.makedirs(self.installDirectory)
        return self.installDirectory

    def _download(self):
        tempDir = tempfile.mkdtemp()
        tempFilePath = os.path.join(tempDir, self.name)
        try:
            urllib.request.urlretrieve(self.url, tempFilePath)
        except Exception as e:
            return Promise.reject(e)
        return tempFilePath

    def _extract(self, tempFilePath):
        self.binaryDirectory = os.path.join(self.installDirectory, self.name)
        try:
            with tarfile.open(tempFilePath, 'r') as tar:
                tar.extractall(path=self.binaryDirectory)
        except Exception as e:
            return Promise.reject(e)
        return self.binaryDirectory

    def _cleanup(self):
        shutil.rmtree(tempfile.gettempdir())

    def _handleError(self, error):
        return Promise.reject(error)
