      }
    }
  }

  dispatchEvent(event) {
    if (this.eventListeners[event.type]) {
      this.eventListeners[event.type].forEach(listener => listener(event));
    }
  }

  open(wasReconnect) {
    if (this.ws && this.ws.readyState !== WebSocket.CLOSED) {
      this.close(1000, 'Connection already open');
      return;
    }
    this.readyState = WebSocket.CONNECTING;
    this.reconnectAttempts = wasReconnect ? this.reconnectAttempts + 1 : 0;
    this.ws = new WebSocket(this.url, this.protocols);
    this.ws.binaryType = 'blob';

    this.ws.onopen = (event) => {
      this.readyState = WebSocket.OPEN;
      this.reconnectAttempts = 0;
      this.protocol = this.ws.protocol;
      this.dispatchEvent(new CustomEvent('open', { detail: event }));
      this.dispatchEvent(new CustomEvent('message', { detail: event }));
    };

